mapper.select:
SELECT mem_no , mem_id , passwd , email , cell_no , create_dt , update_dt FROM mem WHERE 1 = 1
AND mem_no = #{memNo}
AND mem_id = #{memId}
ORDER BY mem_no DESC LIMIT 3

mapper.insert:
INSERT INTO mem ( mem_id
                , passwd
                , email
                , cell_no
                , create_dt
                , update_dt)
VALUES (
        #{memId}
        ,#{passwd}
        ,#{email}
        ,#{cellNo}
        ,CURRENT_TIMESTAMP
        ,CURRENT_TIMESTAMP
       )
